import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Categories.css';

const Categories = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const categories = [
    { id: 1, name: "Laptops", image: "/images/categories/laptops.jpg" },
    {
      id: 2,
      name: "Smartphones",
      image: "https://fdn2.gsmarena.com/vv/pics/apple/apple-iphone-14-pro-3.jpg" },
    { id: 3, name: "Tablets", image: "/images/categories/tablets.jpg" },
    { id: 4, name: "Accessories", image: "/images/categories/accessories.jpg" },
    { id: 5, name: "Gaming", image: "/images/categories/gaming.jpg" },
    { id: 6, name: "Audio", image: "/images/categories/audio.jpg" },
  ];

  const handleCategoryClick = (category) => {
    navigate(`/category/${category.id}`, { state: location });
  };

  return (
    <div className="categories-container">
      <h1>Shop by Category</h1>
      <div className="category-grid">
        {categories.map(category => (
          <div 
            key={category.id} 
            className="category-card"
            onClick={() => handleCategoryClick(category)}
          >
            <img src={category.image} alt={category.name} className="category-image" />
            <h3>{category.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Categories;
