import React, { useState, useEffect } from 'react';
import ProductCard from './components/ProductCard';
import Chatbot from './components/Chatbot';
import './App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(response => response.json())
      .then(data => setProducts(data));
  }, []);

  const addToCart = (product) => {
    setCart(prevCart => [...prevCart, product]);
    // Here you would typically make an API call to add to cart
  };

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <Router>
      <div className="App">
        <nav className="main-nav">
          <Link to="/" className="logo">TechStore</Link>
          <div className="nav-links">
            <Link to="/">Home</Link>
            <Link to="/categories">Categories</Link>
            <Link to="/search">Search</Link>
            <Link to="/cart" className="cart-link">
              <span className="cart-icon">ðŸ›’</span>
              <span className="cart-count">{cart.length}</span>
            </Link>
          </div>
        </nav>

        <Routes>
          <Route path="/" element={
            <div className="App-header">
              <h1>Electronics Store</h1>
              <div className="cart-badge">
                Cart ({cart.length})
              </div>
            </div>
          } />
          <Route path="/" element={
            <main className="products-grid">
              {products.map(product => (
                <ProductCard
                  key={product.id}
                  product={product}
                  onAddToCart={addToCart}
                  onClick={() => handleProductClick(product.id)}
                />
              ))}
            </main>
          } />
          <Route path="/product/:id" element={<ProductDetail />} />
          <Route path="/cart" element={<ShoppingCart />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/categories" element={<Categories />} />
          <Route path="/search" element={<Search />} />
        </Routes>

        <footer className="footer">
          <div className="footer-content">
            <div className="footer-section">
              <h3>Customer Service</h3>
              <ul>
                <li><a href="/contact">Contact Us</a></li>
                <li><a href="/returns">Returns</a></li>
                <li><a href="/shipping">Shipping Info</a></li>
              </ul>
            </div>
            <div className="footer-section">
              <h3>Quick Links</h3>
              <ul>
                <li><a href="/about">About Us</a></li>
                <li><a href="/faq">FAQ</a></li>
                <li><a href="/privacy">Privacy Policy</a></li>
              </ul>
            </div>
            <div className="footer-section">
              <h3>Connect With Us</h3>
              <div className="social-links">
                <a href="#" className="social-icon">Facebook</a>
                <a href="#" className="social-icon">Twitter</a>
                <a href="#" className="social-icon">Instagram</a>
              </div>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; {new Date().getFullYear()} TechStore. All rights reserved.</p>
          </div>
        </footer>
      </div>
    </Router>
  );

  function Home() {
    return (
      <div className="home-page">
        <section className="hero">
          <h1>Welcome to TechStore</h1>
          <p>Your one-stop shop for all your tech needs</p>
          <div className="featured-products">
            {/* Add featured products here */}
          </div>
        </section>
      </div>
    );
  }

  function Categories() {
    return (
      <div className="categories-page">
        <h1>Shop by Category</h1>
        <div className="category-grid">
          {/* Add category cards here */}
        </div>
      </div>
    );
  }

  function Search() {
    return (
      <div className="search-page">
        <h1>Search Results</h1>
        <div className="search-results">
          {/* Add search results here */}
        </div>
      </div>
    );
  }
}

export default App;
