import React, { useEffect, createContext, useContext, useState } from 'react';
import { createBrowserRouter, RouterProvider, Link, Routes, Route } from 'react-router-dom';
import ProductCard from './components/ProductCard';
import Chatbot from './components/Chatbot';
import ProductDetail from './pages/ProductDetail';
import ShoppingCart from './pages/ShoppingCart';
import Checkout from './pages/Checkout';
import Categories from './pages/Categories';
import Search from './pages/Search';
import './App.css';

// Create a context for products
export const ProductsContext = createContext();

function App() {
  const [cart, setCart] = React.useState([]);
  const [products, setProducts] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/products');
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data);
        setError(null);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching products:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCart(prev => [...prev, product]);
    // Here you would typically make an API call to add to cart
  };

  // Create a value object for the context
  const value = {
    products,
    loading,
    error,
    addToCart,
    cart
  };

  const router = createBrowserRouter([
    {
      path: '/',
      element: (
        <div className="app-container">
          <nav className="main-nav">
            <Link to="/" className="logo">TechStore</Link>
            <div className="nav-links">
              <Link to="/" className="nav-link">Home</Link>
              <Link to="/categories" className="nav-link">Categories</Link>
              <Link to="/search" className="nav-link">Search</Link>
              <Link to="/cart" className="cart-link">
                <span className="cart-icon">🛒</span>
                <span className="cart-count">{cart.length}</span>
              </Link>
            </div>
          </nav>
          <div className="content">
            <Routes>
              <Route path="/" element={
                <div className="home-page">
                  <section className="hero">
                    <h1>Welcome to TechStore</h1>
                    <p>Your one-stop shop for all your tech needs</p>
                    <div className="featured-products">
                      {/* Add featured products here */}
                    </div>
                  </section>
                  {loading ? (
                    <div className="loading-container">
                      <div className="loading-spinner"></div>
                      <p>Loading products...</p>
                    </div>
                  ) : error ? (
                    <div className="error-container">
                      <p>Error loading products: {error}</p>
                      <button onClick={() => window.location.reload()}>Try again</button>
                    </div>
                  ) : (
                    <main className="products-grid">
                      {products.map(product => (
                        <ProductCard
                          key={product.id}
                          product={product}
                          onAddToCart={addToCart}
                        />
                      ))}
                    </main>
                  )}
                </div>
              } />
              <Route path="/product/:id" element={<ProductDetail />} />
              <Route path="/cart" element={<ShoppingCart />} />
              <Route path="/checkout" element={<Checkout />} />
              <Route path="/categories" element={<Categories />} />
              <Route path="/search" element={<Search />} />
            </Routes>
          </div>
          <footer className="footer">
            <div className="footer-content">
              <div className="footer-section">
                <h3>Customer Service</h3>
                <ul className="footer-links">
                  <li><Link to="/contact" className="footer-link">Contact Us</Link></li>
                  <li><Link to="/returns" className="footer-link">Returns</Link></li>
                  <li><Link to="/shipping" className="footer-link">Shipping Info</Link></li>
                </ul>
              </div>
              <div className="footer-section">
                <h3>Quick Links</h3>
                <ul className="footer-links">
                  <li><Link to="/about" className="footer-link">About Us</Link></li>
                  <li><Link to="/faq" className="footer-link">FAQ</Link></li>
                  <li><Link to="/privacy" className="footer-link">Privacy Policy</Link></li>
                </ul>
              </div>
              <div className="footer-section">
                <h3>Connect With Us</h3>
                <div className="social-links">
                  <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className="social-link">Facebook</a>
                  <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" className="social-link">Twitter</a>
                  <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className="social-link">Instagram</a>
                </div>
              </div>
              <div className="footer-bottom">
                <p className="copyright">&copy; {new Date().getFullYear()} TechStore. All rights reserved.</p>
              </div>
            </div>
          </footer>
          <Chatbot />
        </div>
      )
    }
  ]);

  return (
    <ProductsContext.Provider value={value}>
      <RouterProvider router={router} />
    </ProductsContext.Provider>
  );
}

export default App;
